function friendly(str) {
  var dates = str.map(function(s) {return s.split('-').map(Number);});
  var start = dates[0];
  var end = dates[1];
  if (str[0] === str[1]) {
    return [readable(start)];
  }
  if (start[0] !== end[0]) {
    if (start[0] + 1 === end[0] && start[1] > end[1]) {
      start[0] = undefined;
      end[0] = undefined;
    }
    return dates.map(readable);
  }
  start[0] = undefined;
  end[0] = undefined;
  if (start[1] !== end[1]) {
    return dates.map(readable);
  }
  end[1] = undefined;
  return dates.map(readable);
}

function readable(arr) {
  var ordD = arr[2] + nth(arr[2]);
  if (!arr[1]) {
    return ordD;
  }
  return MONTH[arr[1]] +  " " + ordD + (!arr[0] ? "" : ", " + arr[0]);
}

